import React, {useState, useEffect} from 'react'

const PedidosClientesComponent = () => {
    //setear los hooks useState
    const [ pedidos_clientes, setPedidosClientes ] = useState([])
    const [ search, setSearch ] = useState("")

    //función para traer los datos de la API
    const URL = 'http://localhost:5000/pedido_cliente'

    const showData = async () => {
        const response = await fetch(URL)
        const data = await response.json()
        // console.log(data)
        setPedidosClientes(data)
    }
    //función de búsqueda
    const searcher = (e) => {
        setSearch(e.target.value)
    }
    //metodo de filtrado 1
    /*  let results = []
    if(!search)
    {
        results = users
    }else{
         results = users.filter( (dato) =>
         dato.name.toLowerCase().includes(search.toLocaleLowerCase())
     )
    } */

    //metodo de filtrado 2
    const results = !search ? pedidos_clientes : pedidos_clientes.filter((dato)=> dato.MOMBRE.toLowerCase().includes(search.toLocaleLowerCase()))

    useEffect( ()=> {
        showData()
    }, [])

    //renderizamos la vista
    return (
        <div>
            <input value={search} onChange={searcher} type="text" placeholder='Search' className='form-control'/>
            <table className='table table-striped table-hover mt-5 shadow-lg'>
                <thead>
                <tr className='bg-curso text-white'>
                    <th>TIPO PEDIDO</th>
                    <th>NUMERO PEDIDO</th>
                    <th>CC CLIENTE</th>

                </tr>
                </thead>
                <tbody>
                { results.map( (pedido_cliente) => (
                    <tr key={pedido_cliente.id}>
                        <td>{pedido_cliente.tipo_pedido}</td>
                        <td>{pedido_cliente.numero_pedido}</td>
                        <td>{pedido_cliente.CEDULA}</td>

                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    )
}
export default PedidosClientesComponent
