import React, {useState, useEffect} from 'react';

const PedidosComponent = () => {
    //Setear los hooks useStates
    const [pedidos, setPedidos] = useState([])
    const [search, setSearch] = useState("")
    //Funcion para obtener los datos de la API
    const URL = 'http://localhost:5000/pedidos'
    const showData = async () => {
        const response = await fetch(URL)
        const data = await response.json()
        // console.log(data)
        setPedidos(data)
    }


    //Funcion de busqueda
    const searcher = (e) => {
        setSearch(e.target.value)
        // console.log(e.target.value)
    }

    //Metodo de filtrado
    // let results = []
    // if (!search) {
    //     results = users
    // } else {
    //     results = users.filter( (dato) =>
    //         dato.NOMBRE.toLowerCase().includes(search.toLocaleLowerCase())
    //     )
    // }

    const results = !search ? pedidos : pedidos.filter((dato)=> dato.tipo_pedido.toLowerCase().includes(search.toLocaleLowerCase()))

    useEffect(() => {
        showData()
    }, [])
    //Renderizacion de la vista
    return (
        <div>
            <input value={search} onChange={searcher} type='text' placeholder='Search' className='form-control'/>
            <table className="table table-striped table-hover mt-5 shadow-lg">
                <thead>
                <tr className='bg-curso text-white'>
                    <th>Número de pedido</th>
                    <th>Tipo de pedido</th>
                    <th>Cédula del cliente</th>

                </tr>
                </thead>
                <tbody>
                { results.map( (pedido) => (
                    <tr key={pedido.id}>
                        <td>{pedido.numero_pedido}</td>
                        <td>{pedido.tipo_pedido}</td>
                        <td>{pedido.cc_cliente}</td>

                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    )
}
export default PedidosComponent
